/*!

=========================================================
* Paper Dashboard React - v1.3.0
=========================================================

* Product Page: https://www.creative-tim.com/product/paper-dashboard-react
* Copyright 2021 Creative Tim (https://www.creative-tim.com)

* Licensed under MIT (https://github.com/creativetimofficial/paper-dashboard-react/blob/main/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React, {useEffect, useState} from "react";
// react plugin used to create charts
import ChartComponent, {Bar, Line, Pie} from "react-chartjs-2";
// reactstrap components
import {
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardTitle,
  Row,
  Col,
} from "reactstrap";
// core components
import {
  dashboard24HoursPerformanceChart,
  dashboardEmailStatisticsChart,
  dashboardNASDAQChart,
} from "variables/charts.js";
import table from "table";
import {Chart} from "chart.js";
import axios from "axios";

function Dashboard() {
  const [APIData, setAPIData] = useState([]);
  const [salles, setsalles] = useState([]);
  const [oc, setoc] = useState([]);
  const [sallesoc, setsallesoc] = useState([]);
  const [sallesnoc, setsallesnoc] = useState([]);
  const [test2, settest] = useState([]);

  useEffect(() => {
    setInterval(() => {
      axios.get(`http://localhost:8081/Electricite/all`)
          .then((response) => {
            settest(response.data)
            console.log(test2);

          })
    },1000)
  }, [])
  useEffect(() => {
    setInterval(() => {
      axios.get(`http://localhost:3000/occupation/salle/créneau%201`)
          .then((response) => {
            setsallesoc(response.data)
            console.log(sallesoc.length);

          })
    },1000)
  }, [])
  useEffect(() => {
    setInterval(() => {
      axios.get(`http://localhost:3000/occupation/salle2/créneau%201`)
          .then((response) => {
            setsallesnoc(response.data)
            console.log(sallesnoc.length);

          })
    },1000)
  }, [])

  useEffect(() => {
    setInterval(() => {
      axios.get(`http://localhost:3000/occupation/salles/a`)
          .then((response) => {
            setAPIData(response.data)
            console.log(APIData.map(e=>e.count.toString()));

          })
    }, 1000);

  }, [])
  useEffect(() => {
    setInterval(() => {
      axios.get(`http://localhost:3000/salles/`)
          .then((response) => {
            setsalles(response.data)

          })
    }, 1000);
  }, [])

  useEffect(() => {
    setInterval(() => {
      axios.get(`http://localhost:3000/occupation/salles/b`)
          .then((response) => {
            setoc(response.data)

          })
    }, 1000);


  }, [])

  const state = {

    series: [76, 67, 61, 90],
    options: {
      chart: {
        height: 390,
        type: 'radialBar',
      },
      plotOptions: {
        radialBar: {
          offsetY: 0,
          startAngle: 0,
          endAngle: 270,
          hollow: {
            margin: 5,
            size: '30%',
            background: 'transparent',
            image: undefined,
          },
          dataLabels: {
            name: {
              show: false,
            },
            value: {
              show: false,
            }
          }
        }
      },
      colors: ['#1ab7ea', '#0084ff', '#39539E', '#0077B5'],
      labels: ['Vimeo', 'Messenger', 'Facebook', 'LinkedIn'],
      legend: {
        show: true,
        floating: true,
        fontSize: '16px',
        position: 'left',
        offsetX: 160,
        offsetY: 15,
        labels: {
          useSeriesColors: true,
        },
        markers: {
          size: 0
        },
        formatter: function(seriesName, opts) {
          return seriesName + ":  " + opts.w.globals.series[opts.seriesIndex]
        },
        itemMargin: {
          vertical: 3
        }
      },
      responsive: [{
        breakpoint: 480,
        options: {
          legend: {
            show: false
          }
        }
      }]
    },


  };

  const config2 = {
    type: 'doughnut',
    data: {
      labels: [
        'salle occupee',
        'salle non occupee',
      ],
      datasets: [{
        label: 'My First Dataset',
        data: [sallesoc.length, sallesnoc.length],
        backgroundColor: [
          '#e2785e',
          '#61e1c7',
        ],
        hoverOffset: 2
