    setInterval(() => {
      axios.get(`http://localhost:3000/occupation/salle/créneau%201`)
          .then((response) => {
            setsallesoc(response.data)

          })
    },1000)
  }, [])
  useEffect(() => {
    setInterval(() => {
      axios.get(`http://localhost:3000/occupation/salle2/créneau%201`)
          .then((response) => {
            setsallesnoc(response.data)

